import React, { useState , useEffect } from "react";
import Logowanie from "./Logowanie";
import RegistrationPage from "./RegistrationPage";
import ChatRoom from "./ChatRoom";
//import '../style/index.css'

function StartWeb() {
    const [page, setPage] = useState('login');
  

    useEffect(() => {
      if(localStorage.getItem("sessionToken")==="token")
      setPage('zalogowany')
    }, []);

    const handleLoginClick = () => {
      if(localStorage.getItem("sessionToken")!=="token"){  
      setPage('login');
      }
      else{
        
    localStorage.setItem('sessionToken'," " );
        setPage('logout');
      }
    };
  
    const handleRegisterClick = () => {
      if(localStorage.getItem("sessionToken")!=="token"){  
        setPage('register');
        }
        else{
          
    localStorage.setItem('sessionToken'," " );
          setPage('logout');
        }
    };
    const handleClick = () => {
      setPage('login');
    };
    const zostan =()=>{
      setPage('zalogowany')
    }
    return (
      <div>
        { page==="logout" ?(
          <div style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '4px', background: '#f5f5f5' }}>
  <span>
    Wyloguj <button onClick={handleClick}>Wyloguj</button>
  </span>
  <p>
    NIE wyloguj <button onClick={zostan}>Zosta≈Ñ</button>
  </p>
</div>

        )
        :(
      <div>
        <p>
       
            <span>
              Don't have an account?   &ensp; <button onClick={handleRegisterClick}>Register here</button>&emsp;
            </span>
            
            <span id="ddd">
                 Already have an account? &ensp;<button onClick={handleLoginClick}>Login here</button>
            </span>
          
          </p>
          
        {page === 'login' ? (
          <Logowanie />
        ) : page === "zalogowany" ? <ChatRoom username={localStorage.getItem("username")} nick={localStorage.getItem("nick")}/>
        : (
          <RegistrationPage />
        )}        
          </div>)
          }
          </div>
    )}
          
export default StartWeb;
