import { useState, useEffect,useRef  } from 'react'
import axios from 'axios';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';
import '../style/easyStyle.css'
function ChatRoom(props){
const [friends, setFriends] = useState([]);
const [rooms, setRooms] = useState([]);
const [currentRoom, setCurrentRoom] = useState(null);
const [content, setContent] = useState('');
const [email, setEmail] = useState('');
const [message, setMessage] = useState('');
const [posts, setPosts] = useState([]);
const [ChatRoomName, setChatRoomName] = useState('');
const [checkedItems, setCheckedItems] = useState([]);
const [stompClient, setStompClient] = useState(null);

const [userData, setUserData] = useState({
  username: props.username,
  receivername: '',
  connected: false
});


useEffect(() => {
  axios.get('http://localhost:8080/getAllUsers')
    .then(response => {
      setFriends(response.data);
    })
    .catch(error => {
      console.log(error);
    });
}, []);

function handleEmailChange(event) {
  setEmail(event.target.value);
}


  const getRooms = async () => {
    try {
      const response = await axios.get('http://localhost:8080/ChatRooms');

      setRooms(response.data);
     
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getFriends();
    getRooms();
  }, []);

  /**
   * znajomi
   */
  const getFriends = async () => {
    try {
      
      const response = await axios.get('http://localhost:8080/getAllUsers');
      setFriends(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const addFriend = async () => {
    try {
        setContent('dodajZnajomego');
  
    } catch (error) {
      console.error(error);
    }
  };

 function handleAddFriend(event) {
    const user1_id_user_id = userData.username;
    const user2_id_user_id = email;
    event.preventDefault();
    axios.post('http://localhost:8080/AddFriend', user1_id_user_id+";"+user2_id_user_id
    , {
      headers: {
        'Content-Type': 'text/plain'
      }
    })  
      .then(response => {
        console.log(response.data);
        setEmail('');
        console.log(response.data);
        setFriends(response.data);
        alert("dodano pomyślnie")
      })
      .catch(error => {
       alert("nie udało się dodać znajomego");
        console.log(error);
      });
  }

  const handleChangeZnajomiCheckboxlist = (event) => {
    const isChecked = event.target.checked;
    const itemId = event.target.value;

    if (isChecked) {
      setCheckedItems((prevItems) => [...prevItems, itemId]);
    } else {
      setCheckedItems((prevItems) => prevItems.filter((id) => id !== itemId));
    }
  };
  const setChatRoomName2 = (event) =>{
    const value = event.target.value;
    setChatRoomName(value);
  }

  /**
   * 
   * CHatroomy
   * @param {*} event 
   */
  const handleAddChatroom = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/AddChatroom', {
      nazwa:ChatRoomName,
      user: userData.username,
      id_realacji: checkedItems,
      });
      console.log('Response:', response.data);
      setChatRoomName(response.data);
      
    } catch (error) {
      console.error('Error:', error);
    }
  };
  const addRoom = async () =>{
    setContent('dodajChatroom');
  }
/**
 * POSTY
 * 
 */



useEffect(() => {
  const socket = new SockJS('http://localhost:8080/ws');
  const client = Stomp.over(socket);
  client.connect({}, () => {
    setStompClient(client);
  });

  return () => {
    if (stompClient) {
      stompClient.disconnect();
    }
  };
}, []);

useEffect(() => {
  if (!stompClient) return;

  const subscription = stompClient.subscribe('/topic/posts', (message) => {
    const receivedPost = JSON.parse(message.body);
    setPosts((prevPosts) => [...prevPosts, receivedPost]);
  });

  return () => {
    subscription.unsubscribe();
  };
}, [stompClient]);


  useEffect(() => {
    // Fetch initial posts from the backend
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:8080/posts1');
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const addPost = () => {
    const post = {
      message: message,
      date: new Date(),
      owner: userData.username, 
      chatroom_id: currentRoom.chatroom_id, 
    };
 
    stompClient.send('/app/addPost', {}, JSON.stringify(post));
    
    setMessage('');
  };

  

  /**
   * KOD HTML'A
   */

  return (
    <div id="container">
  
      <div id="left">
      <h2>Znajomi</h2>
      <ul>
        {friends.map((friend) => (
          <li key={friend.user_id}>
            <span class="user-id">{friend.user_id}</span>
            <span class="nick">{friend.nick}</span>
          </li>
        ))}
      </ul>
      <button onClick={addFriend}>Dodaj znajomego</button>
    </div>



       <div id ='center'>
        {content === "" ? ( <div>
         {currentRoom ? (
//Chatroom


<div class="chat-container">
  <h2 class="room-name">{currentRoom.nazwa}</h2>
  <div class="friend-list-container">
    <ul class="friend-list">
      {currentRoom.id_realacji.map((friend) => (
        <li key={friend.relacja}>{friend.user2_id}</li>
      ))}
    </ul>
  </div>
  <div class="message-list-container">
    <ul class="message-list">
      {posts.map((post, index) => (
        <li key={index} >
          <div class="message-info">
            <div class="message-owner">{post.owner}</div>
            <div class="message-date">{post.date}</div>
          </div>
          <div class="message-content">{post.message}</div>
        </li>
      ))}
    </ul>
  </div>
  <div class="input-container">
    <input
      type="text"
      class="message-input"
      placeholder="Dodaj post"
      value={message}
      onChange={(e) => setMessage(e.target.value)}
    />
    <button class="add-post-button" onClick={addPost}>Dodaj post</button>
  </div>
</div>




         ) : 
         //wybieranie pokoju 
         (
           <p>Wybierz pokój</p>
         )}
         </div> ): 
         (<div>{content === "dodajZnajomego"?(<div>
                  <form onSubmit={handleAddFriend}>
                        <label>
                        email:
                        <input type="text" value={email} onChange={handleEmailChange} />
                        </label>
                        <br />
                        <button type="submit">Dodaj Znajomego</button>
                    </form>
         </div>)
         : content==="dodajChatroom"? <div>
                <form onSubmit={handleAddChatroom}>
                    <label>
                        nazwa chatroom'u:
                        <input type='text'
                              onChange={setChatRoomName2}
                        />
                    </label>
                    <br />
                    <div>
                      {friends.map((item) => (
                        <label key={item.user_id}>
                          <input
                            type="checkbox"
                            value={item.user_id}
                            checked={checkedItems.includes(item.user_id)}
                            onChange={handleChangeZnajomiCheckboxlist}
                          />
                          {item.nick}
                        </label>
                      ))}
                    </div>
                    <button type='submit'>Dodaj ChatRoom</button>
                </form>
         </div>
         :<div>error</div>}</div>)
}
       </div>
    <div id='right'>
         <h2>Pokoje</h2>
         <ul>
           {rooms.map((room) => (
             <li key={room.chatroom_id} onClick={() => setCurrentRoom(room)}>
               {room.nazwa}
             </li>
           ))}
         </ul>
         <button onClick={addRoom}>Dodaj pokój</button>
       </div>
</div>
   );         
}
export default ChatRoom;
