import { useState, useEffect } from 'react'
import axios from 'axios';
import '../style/easyStyle.css'
function ChatRoom(props){
const [friends, setFriends] = useState([]);
const [rooms, setRooms] = useState([]);
const [currentRoom, setCurrentRoom] = useState(null);
const [content, setContent] = useState('');
const [email, setEmail] = useState('');
const [ChatRoomName, setChatRoomName] = useState('');
const [checkedItems, setCheckedItems] = useState({});
const [userData, setUserData] = useState({
  username: props.username,
  receivername: '',
  connected: false
});

useEffect(() => {
  axios.get('http://localhost:8080/getAllUsers')
    .then(response => {
      setFriends(response.data);
    })
    .catch(error => {
      console.log(error);
    });
}, []);

function handleEmailChange(event) {
  setEmail(event.target.value);
}
const getFriends = async () => {
    try {
      
      const response = await axios.get('http://localhost:8080/getAllUsers');
      setFriends(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getRooms = async () => {
    try {
      const response = await axios.get('http://localhost:8080/ChatRooms');

      setRooms(response.data);
     
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getFriends();
    getRooms();
  }, []);

  const addFriend = async () => {
    try {
        setContent('dodajZnajomego');
  
    } catch (error) {
      console.error(error);
    }
  };

 function handleAddFriend(event) {
    const user1_id_user_id = userData.username;
    const user2_id_user_id = email;
    event.preventDefault();
    axios.post('http://localhost:8080/AddFriend', user1_id_user_id+";"+user2_id_user_id
    , {
      headers: {
        'Content-Type': 'text/plain'
      }
    })  
      .then(response => {
        console.log(response.data);
        setEmail('');
        console.log(response.data);
      //  setFriends(response.data);
      })
      .catch(error => {
       
        console.log(error);
      });
  }
  function handleAddChatroom(event){
    event.preventDefault();
    axios.post('/api/friends', { ChatRoomName}) //zmieniÄ‡ endpoint
      .then(response => {
        console.log(response.data);
        setChatRoomName('');
        setRooms(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }

  const addRoom = async () =>{
    setContent('dodajChatroom');
  }
  const addPost = async() =>{

  }

  
  const handleChangeZnajomiCheckboxlist = (event) => {
    setCheckedItems({
      ...checkedItems,
      [event.target.name]: event.target.checked,
    });
  };
  return (

    <div id="container">
  
      <div id='left'>
        <h2>Znajomi</h2>
        <ul>
           {friends.map((friend) => (
             <li key={friend.email}>{friend.nick}</li>
           ))}
         </ul>
         <button onClick={addFriend}>Dodaj znajomego</button>
       </div> 

       <div id ='center'>
        {content === "" ? ( <div>
         {currentRoom ? (
           <div>
             <h2>{currentRoom.nazwa}</h2>
             <ul>
               {currentRoom.relacja.map((friend) => (
                 <li key={friend.relacja}>{friend.user2_id}</li>
               ))}
             </ul>
             <input type="text" placeholder="Dodaj post" />
             <button onClick={addPost}>Dodaj post</button>
           </div>
         ) : (
           <p>Wybierz pokÃ³j</p>
         )}
         </div> ): 
         (<div>{content === "dodajZnajomego"?(<div>
                  <form onSubmit={handleAddFriend}>
                        <label>
                        email:
                        <input type="text" value={email} onChange={handleEmailChange} />
                        </label>
                        <br />
                        <button type="submit">Dodaj Znajomego</button>
                    </form>
         </div>)
         : content==="dodajChatroom"? <div>
                <form onSubmit={handleAddChatroom}>
                    <label>
                        nazwa chatroom'u:
                        <input type='text'/>
                    </label>
                    <br />
                    <div>
                      {friends.map((item) => (
                        <label key={item.nick}>
                          <input
                            type="checkbox"
                            name="znajomi"
                            checked={checkedItems[item.label] || false}
                            onChange={handleChangeZnajomiCheckboxlist}
                          />
                          {item.label}
                        </label>
                      ))}
                    </div>
                    <button type='submit'>Dodaj ChatRoom</button>
                </form>
         </div>
         :<div>error</div>}</div>)
}
       </div>
    <div id='right'>
         <h2>Pokoje</h2>
         <ul>
           {rooms.map((room) => (
             <li key={room.chatroom_id} onClick={() => setCurrentRoom(room)}>
               {room.nazwa}
             </li>
           ))}
         </ul>
         <button onClick={addRoom}>Dodaj pokÃ³j</button>
       </div>
</div>
   );         
}
export default ChatRoom;
