import React, { useState,useEffect } from "react";
import ChatRoom from "./ChatRoom";
import ReactDomServer from 'react-dom/server';

import ReactDOM from "react-dom";



import "../style/style.css";

function Logowanie() {
  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  

  const errors = {
    uname: "invalid username",
    pass: "invalid password"
  };

    const handleSubmit = (event) => {
        event.preventDefault();
        var { uname, pass } = document.forms[0];
        
        fetch('http://localhost:8080/getUserById/'+uname.value)
            .then((res) => res.json())
            .then((user) =>{
                     // Compare user info
                if (user.length !==0) {
                    if (user[0].password === pass.value) {
                    setIsSubmitted(true);
                    
                    } else {
                    console.log(user[0].password);
                    console.log(pass.value);
                    // Invalid password
                    setErrorMessages({ name: "pass", message: errors.pass });
                    
                    }
                } else {
                    // Username not found
                    setErrorMessages({ name: "uname", message: errors.uname });
                }
                }
            )
            .catch((err) => 
            {
                console.log(err.message);
                setErrorMessages({ name: "uname", message: errors.uname });
            });
     };
 
  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    
    <div className="form">
        <div className="title">LOGOWANIE</div>
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>Username </label>
          <input type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input-container">
          <label>Password </label>
          <input type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="button-container">
          <input type="submit" />
        </div>
      </form>
    </div>
  );
  

  function rejestruj () {
    
    var { email1,nick1, pass1 } = document.getElementById("form_rej_id");
    var data ={"email":email1,"nick":nick1,"password":pass1}
  
    fetch("http://localhost:8080/addUser", {
      method: "POST", 
      mode: "cors", 
      cache: "no-cache", 
      credentials: "same-origin", 
      headers: {        "Content-Type": "application/json",      },
      redirect: "follow", 
      referrerPolicy: "no-referrer", 
      body: JSON.stringify(data), 
    });
    console.log(data);
    alert('A name was submitted: ' + this.state.value);
   
}

  const loginForm = (
    <div className="form">
    <div className="title">Rejestracja</div>
    <form onSubmit="rejestruj()" id="form_rej_id">
    <div className="input-container">
    <label>e-mail </label> <input type="text" name="email1" required />
        </div>   <div className="input-container">    
        <label>nick </label>     <input type="text" name="nick1" required />
        </div>    <div className="input-container">     
        <label>Password </label>    <input type="password" name="pass1" required />
    </div>
    <div className="button-container">
      <input type="submit" />
    </div>
  </form>
</div>
  );
 const logowanie =()=>{
    var x= document.getElementById("log_id").innerHTML= 
    ReactDomServer.renderToString(renderForm);

  }
  const rejestracja= ()=> {
    document.getElementById("log_id").innerHTML = 
    ReactDomServer.renderToString(loginForm); 
}
  return (
  
    <div> 
    {isSubmitted ? <div><ChatRoom></ChatRoom></div> : 
    <div className="logowanie">
        
      <button type="button" onClick={rejestracja}> ZAREJESTRUJ SIĘ</button>
      <button type="button" onClick={logowanie}>LOGOWANIE</button>
      <div className="login-form" id="log_id">
        
        {renderForm}
      </div>
    </div>
}
    </div>
  );
}

export default Logowanie;
