import { useState, useEffect } from 'react'
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';

function ChatRoom(){
const [friends, setFriends] = useState([]);
const [rooms, setRooms] = useState([]);
const [currentRoom, setCurrentRoom] = useState(null);

const getFriends = async () => {
    try {
      const response = await axios.get('http://localhost:8080/getAllUsers');
      setFriends(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getRooms = async () => {
    try {
      const response = await axios.get('http://localhost:8080/ChatRooms');
      setRooms(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getFriends();
    getRooms();
  }, []);
  const addFriend = async () => {
    try {
      const response = await axios.post('http://localhost:8080/addUser');
      setFriends([...friends, response.data]);
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div>
  <Router>
      <Layout>
        <Routes>
    <Route exact path="/">
      <div>
        <h2>Znajomi</h2>
        <ul>
           {friends.map((friend) => (
             <li key={friend.id}>{friend.name}</li>
           ))}
         </ul>
         <button onClick={addFriend}>Dodaj znajomego</button>
       </div> 
    </Route>
    <Route path="/pokoje">
    <div>
         <h2>Pokoje</h2>
         <ul>
           {rooms.map((room) => (
             <li key={room.id} onClick={() => setCurrentRoom(room)}>
               {room.name}
             </li>
           ))}
         </ul>
         <button>Dodaj pok√≥j</button>
       </div>
    </Route>
    <Route path="/znajomi">
    <div>
         {currentRoom ? (
           <div>
             <h2>{currentRoom.name}</h2>
             <ul>
               {currentRoom.friends.map((friend) => (
                 <li key={friend.id}>{friend.name}</li>
               ))}
             </ul>
             <input type="text" placeholder="Dodaj post" />
             <button>Dodaj post</button>
           </div>
         ) : (
           <p>Wybierz pok√≥j</p>
         )}
       </div>
     
    </Route>
    </Routes>
      </Layout>
    </Router>
</div>
     
   );         
}
export default ChatRoom;
