import React, { useState } from "react";
import Logowanie from "./Logowanie";
import RegistrationPage from "./RegistrationPage";
import ChatRoom from "./ChatRoom";
//import '../style/index.css'

function StartWeb() {
    const [page, setPage] = useState('login');
  

    const handleLoginClick = () => {
      if(localStorage.getItem("session")!=="token"){  
      setPage('login');
      }
      else{
        
    localStorage.setItem('sessionToken'," " );
        setPage('login');
      }
    };
  
    const handleRegisterClick = () => {
      if(localStorage.getItem("session")!=="token"){  
        setPage('register');
        }
        else{
          
    localStorage.setItem('sessionToken'," " );
          setPage('register');
        }
    };
    const handleClick = () => {
      setPage('login');
    };
    return (
      <div>
        { page==="logout" ?(
        <div>
             <span>
              Wyloguj <button onClick={handleClick}>Wyloguj</button>
            </span>
        </div>
        )
        :(
      <div>
        <p>
       
            <span>
              Don't have an account?   &emsp; <button onClick={handleRegisterClick}>Register here</button>&emsp;
            </span>
            
            <span id="ddd">
                 Already have an account? <button onClick={handleLoginClick}>Login here</button>
            </span>
          
          </p>
          
        {page === 'login' ? (
          <Logowanie />
        ) : page === "zalogowany" ? <ChatRoom></ChatRoom> 
        : (
          <RegistrationPage />
        )}        
          </div>)
          }
          </div>
    )}
          
export default StartWeb;
