import { useState, useEffect } from 'react'
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';

function ChatRoom(){
const [friends, setFriends] = useState([]);
const [rooms, setRooms] = useState([]);
const [currentRoom, setCurrentRoom] = useState(null);

const getFriends = async () => {
    try {
      const response = await axios.get('adres-endpointu/znajomi');
      setFriends(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getRooms = async () => {
    try {
      const response = await axios.get('adres-endpointu/pokoje');
      setRooms(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getFriends();
    getRooms();
  }, []);
  const addFriend = async () => {
    try {
      const response = await axios.post('adres-endpointu/dodaj-znajomego');
      setFriends([...friends, response.data]);
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div>
  <Navbar />
  <Routes>
    <Route exact path="/">
      <div>
        <h2>Znajomi</h2>
        <ul>
           {friends.map((friend) => (
             <li key={friend.id}>{friend.name}</li>
           ))}
         </ul>
         <button onClick={addFriend}>Dodaj znajomego</button>
       </div> 
    </Route>
    <Route path="/pokoje">
    <div>
         <h2>Pokoje</h2>
         <ul>
           {rooms.map((room) => (
             <li key={room.id} onClick={() => setCurrentRoom(room)}>
               {room.name}
             </li>
           ))}
         </ul>
         <button>Dodaj pokój</button>
       </div>
    </Route>
    <Route path="/znajomi">
    <div>
         {currentRoom ? (
           <div>
             <h2>{currentRoom.name}</h2>
             <ul>
               {currentRoom.friends.map((friend) => (
                 <li key={friend.id}>{friend.name}</li>
               ))}
             </ul>
             <input type="text" placeholder="Dodaj post" />
             <button>Dodaj post</button>
           </div>
         ) : (
           <p>Wybierz pokój</p>
         )}
       </div>
     
    </Route>
  </Routes>
</div>
     
   );         
}
export default ChatRoom;
