import { useState, useEffect } from 'react'
import axios from 'axios';
import { BrowserRouter as Router, Routes,Link, Route } from 'react-router-dom';
import Navbar from './Navbar';

function ChatRoom(){
const [friends, setFriends] = useState([]);
const [rooms, setRooms] = useState([]);
const [currentRoom, setCurrentRoom] = useState(null);

const getFriends = async () => {
    try {
      const response = await axios.get('http://localhost:8080/getAllUsers');
      setFriends(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getRooms = async () => {
    try {
      const response = await axios.get('http://localhost:8080/ChatRooms');

      setRooms(response.data);
     
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getFriends();
    getRooms();
  }, []);
  const addFriend = async () => {
    try {
      const response = await axios.post('http://localhost:8080/addUser');
      setFriends([...friends, response.data]);
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div>
         <ul>
    <li>
      <Link to="/">Pokoje</Link>
    </li>
    <li>
      <Link to="/znajomi">Znajomi</Link>
    </li>
  </ul>
<Router>
  <Routes>
    <Route exact path="/" element={
      <div id='0'>
        <h2>Znajomi</h2>
        <ul>
           {friends.map((friend) => (
             <li key={friend.email}>{friend.nick}</li>
           ))}
         </ul>
         <button onClick={addFriend}>Dodaj znajomego</button>
       </div> 
    }/>
    <Route path="/pokoje" element={
    <div id='1'>
         <h2>Pokoje</h2>
         <ul>
           {rooms.map((room) => (
             <li key={room.chatroom_id} onClick={() => setCurrentRoom(room)}>
               {room.nazwa}
             </li>
           ))}
         </ul>
         <button>Dodaj pokój</button>
       </div>
    }/>
    <Route path="/znajomi" element={
    <div id ='2'>
         {currentRoom ? (
           <div>
             <h2>{currentRoom.name}</h2>
             <ul>
               {currentRoom.friends.map((friend) => (
                 <li key={friend.id}>{friend.name}</li>
               ))}
             </ul>
             <input type="text" placeholder="Dodaj post" />
             <button>Dodaj post</button>
           </div>
         ) : (
           <p>Wybierz pokój</p>
         )}
       </div>
    }/>
  </Routes>
  </Router>
</div>
     
   );         
}
export default ChatRoom;
