import React, { createContext, useContext,useState } from "react";

import Logowanie from "./Logowanie";
import RegistrationPage from "./RegistrationPage";
import '../style/index.css'
const MyContext = createContext();
function StartWeb() {
  
    const [page, setPage] = useState('login');
  
    const handleLoginClick = () => {
      if(localStorage.getItem("session")!=="token"){  
      setPage('login');
      }
      else{
        setPage('logout');
    localStorage.setItem('sessionToken'," " );
      }
    };
  
    const handleRegisterClick = () => {
      if(localStorage.getItem("session")!=="token"){  
        setPage('register');
        }
        else{
          setPage('logout');
        }
    };
    const handleClick = () => {
      setPage('login');
    };
    return (
      <div>
        { page==="logout" ?(
        <div>
             <span>
              Wyloguj <button onClick={handleClick}>Wyloguj</button>
            </span>
        </div>
        )
        :(
      <div>
        <p>
          {page === 'login' ? (
            <span>
              Don't have an account? <button onClick={handleRegisterClick}>Register here</button>
            </span>
          ) : (
            <span id="ddd">
              Already have an account? <button onClick={handleLoginClick}>Login here</button>
            </span>
          )}
          </p>
          
        {page === 'login' ? (
          <Logowanie />
        ) : (
          <RegistrationPage />
        )}        
          </div>)
          }
          </div>
    )}
          
export default StartWeb;
