import { useState, useEffect } from 'react'
import axios from 'axios';
import '../style/easyStyle.css'
import AddFriend from './AddFriend';
function ChatRoom(){
const [friends, setFriends] = useState([]);
const [rooms, setRooms] = useState([]);
const [currentRoom, setCurrentRoom] = useState(null);
const [content, setContent] = useState('');
const getFriends = async () => {
    try {
      const response = await axios.get('http://localhost:8080/getAllUsers');
      setFriends(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getRooms = async () => {
    try {
      const response = await axios.get('http://localhost:8080/ChatRooms');

      setRooms(response.data);
     
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getFriends();
    getRooms();
  }, []);
  const addFriend = async () => {
    try {
        setContent('dodajZnajomego');
  
    } catch (error) {
      console.error(error);
    }
  };


  const addRoom = async () =>{
    setContent('dodajChatroom');
  }
  return (

    <div id="container">
  
      <div id='left'>
        <h2>Znajomi</h2>
        <ul>
           {friends.map((friend) => (
             <li key={friend.email}>{friend.nick}</li>
           ))}
         </ul>
         <button onClick={addFriend}>Dodaj znajomego</button>
       </div> 

       <div id ='center'>
        {content}
         {currentRoom ? (
           <div>
             <h2>{currentRoom.nazwa}</h2>
             <ul>
               {currentRoom.relacja.map((friend) => (
                 <li key={friend.relacja}>{friend.user2_id}</li>
               ))}
             </ul>
             <input type="text" placeholder="Dodaj post" />
             <button onClick={addRoom}>Dodaj post</button>
           </div>
         ) : (
           <p>Wybierz pokój</p>
         )}
       </div>
    <div id='right'>
         <h2>Pokoje</h2>
         <ul>
           {rooms.map((room) => (
             <li key={room.chatroom_id} onClick={() => setCurrentRoom(room)}>
               {room.nazwa}
             </li>
           ))}
         </ul>
         <button>Dodaj pokój</button>
       </div>
</div>
   );         
}
export default ChatRoom;
