import React, { useState,useEffect } from "react";
import ChatRoom from "./ChatRoom";
import ReactDOM from "react-dom";



import "../style/style.css";

function Logowanie() {
  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  

  const errors = {
    uname: "invalid username",
    pass: "invalid password"
  };

    const handleSubmit = (event) => {
        event.preventDefault();
        var { uname, pass } = document.forms[0];
        
        fetch('http://localhost:8080/getUserById/'+uname.value)
            .then((res) => res.json())
            .then((user) =>{
                     // Compare user info
                if (user.length !==0) {
                    if (user[0].password === pass.value) {
                    setIsSubmitted(true);
                    
                    } else {
                    console.log(user[0].password);
                    console.log(pass.value);
                    // Invalid password
                    setErrorMessages({ name: "pass", message: errors.pass });
                    
                    }
                } else {
                    // Username not found
                    setErrorMessages({ name: "uname", message: errors.uname });
                }
                }
            )
            .catch((err) => 
            {
                console.log(err.message);
                setErrorMessages({ name: "uname", message: errors.uname });
            });
        
     
       
     };
 
  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>Username </label>
          <input type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input-container">
          <label>Password </label>
          <input type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="button-container">
          <input type="submit" />
        </div>
      </form>
    </div>
  );


  function rejestracja(){

   
    document.getElementById("log_id").innerHTML = 
    '  <div>\n        <form onSubmit={rejestruj}>\n    <div className="input-container">\n      <label>e-mail </label>\n      <input type="text" name="e-mail" required />\n '
    +'     </div>\n   <div className="input-container">\n      <label>nick </label>\n      <input type="text" name="nick" required />\n '
    +' </div>\n    <div className="input-container">\n      <label>Password </label>\n      <input type="password" name="pass" required />\n  '
    +' </div>\n    <div className="button-container">\n      <input type="submit" />\n    </div>\n  </form> </div>'
    
  }
  return (
  
    <div> 
    {isSubmitted ? <div><ChatRoom></ChatRoom></div> : 
    <div className="logowanie">
        
      <button type="button" onClick={rejestracja}> ZAREJESTRUJ SIÄ˜</button>
      <div className="login-form" id="log_id">
        <div className="title">Sign In</div>
        {renderForm}
      </div>
    </div>
}
    </div>
  );
}

export default Logowanie;
